function ajax(t,e){fetch(t).then(function(t){return t.json()}).then(function(t){e(t)})}function makeChart(t){const e=t;console.log(e);const n=document.querySelector("#myCanvas");var a=n.getContext("2d"),s=e.map(function(t){return t.stats.speed}),o=e.map(function(t){return t.stats.hp}),r=e.map(function(t){return t.stats.spec_def}),c=e.map(function(t){return t.stats.spec_att}),u=e.map(function(t){return t.stats.defense}),t=e.map(function(t){return t.stats.attack});new Chart(a,{type:"bar",data:{labels:["HP","Speed","Sp. Defense","Sp. Attack","Defense","Attack"],datasets:[{label:"Pokemon Stats",backgroundColor:"rgb(226,53,13)",borderColor:"rgb(226,53,13)",data:o,speed:s,spec_def:r,spec_att:c,defense:u,attack:t}]},options:{responsive:!1,scales:{yAxes:[{ticks:{beginAtZero:!0}}]}}})}function init(){ajax("./data/pokemon.min.json",makeChart)}init();